 "<html>\n" +
 "<head>\n" +
 " <meta charset=\"UTF-8\">\n" +
 " <style>\n" +
 " #map { height: 100%; width: 100%; }\n" +
 " html, body { height: 100%; margin: 0; padding: 0; }\n" +
 " #error-message { display: none; text-align: center; padding: 20px; }\n" +
 " </style>\n" +
 "</head>\n" +
 "<body>\n" +
 " <div id=\"map\"></div>\n" +
 " <div id=\"error-message\">Could not load map for this location.</div>\n" +
 " <script>\n" +
 " function initMap() {\n" +
 " const geocoder = new google.maps.Geocoder();\n" +
 " const address = \"" + hebergement.getAdresse().replace("\"", "\\\"") + "\";\n" +
 " \n" +
