 " geocoder.geocode({ 'address': address }, function(results, status) {\n" +
 " if (status === 'OK') {\n" +
 " const map = new google.maps.Map(document.getElementById('map'), {\n" +
 " zoom: 15,\n" +
 " center: results[0].geometry.location,\n" +
 " mapTypeId: 'roadmap'\n" +
 " });\n" +
 " \n" +
 " // Add a marker\n" +
 " new google.maps.Marker({\n" +
 " map: map,\n" +
 " position: results[0].geometry.location,\n" +
 " title: \"" + hebergement.getNomHeberg().replace("\"", "\\\"") + "\"\n" +
 " });\n" +
 " } else {\n" +
 " // If geocoding fails, show a fallback map centered on a default location\n" +
 " const map = new google.maps.Map(document.getElementById('map'), {\n" +
 " zoom: 4,\n" +
 " center: {lat: 46.2276, lng: 2.2137}, // Center of France\n" +
 " mapTypeId: 'roadmap'\n" +
 " });\n" +
 " document.getElementById('error-message').style.display = 'block';\n" +
 " document.getElementById('error-message').innerHTML = \n" +
 " 'Could not find exact location for address: ' + address;\n" +
 " }\n" +
 " });\n" +
 " }\n" +
 " </script>\n" +
 " <script src=\"https://maps.googleapis.com/maps/api/js?key=" + apiKey + "&callback=initMap\" async defer></script>\n" +
 "</body>\n" +
 "</html>";

 mapWebView.getEngine().loadContent(htmlContent);

 } catch (Exception e) {
 e.printStackTrace();
 mapWebView.getEngine().loadContent("<html><body><p>Error: " + e.getMessage() + "</p></body></html>");
 }
}
