 HBox.setHgrow(hebergementName, javafx.scene.layout.Priority.ALWAYS);
 nameAndRating.getChildren().addAll(hebergementName, starsContainer, ratingText);
 hebergementContainer.getChildren().add(nameAndRating);

 HBox priceAndButtons = new HBox(10);
 priceAndButtons.setPadding(new Insets(5, 0, 5, 0));
 priceAndButtons.setAlignment(Pos.CENTER_LEFT);

 Text priceText = new Text("TND" + hebergement.getPrixHeberg() + "/nuit");
 priceText.setStyle("-fx-font-size: 14px; -fx-font-weight: bold; -fx-text-fill: #3b9a9a;");
 priceAndButtons.getChildren().add(priceText);

 Button consultButton = new Button("Détails");
 consultButton.setStyle("-fx-background-color: #FA7335; -fx-text-fill: white; -fx-font-weight: bold;");
 consultButton.setOnAction(event -> openHebergementDetails(hebergement));

 priceAndButtons.getChildren().addAll(consultButton);
 hebergementContainer.getChildren().add(priceAndButtons);

 return hebergementContainer;
}

/**
 * Creates a star rating display based on the given rating value
 */
private HBox createStarRating(double rating) {
 HBox starsBox = new HBox(2);
 starsBox.setAlignment(Pos.CENTER);

 // Load star images
 Image fullStar = new Image(getClass().getResourceAsStream("/images/full_star.png"));
 Image halfStar = new Image(getClass().getResourceAsStream("/images/half_star.png"));
 Image emptyStar = new Image(getClass().getResourceAsStream("/images/empty_star.png"));

 // If you don't have star images, you can use Unicode stars instead
 // and remove the Image loading code above

 for (int i = 1; i <= 5; i++) {
 ImageView starView;

 if (rating >= i) {
 // Full star
 starView = new ImageView(fullStar);
 } else if (rating >= i - 0.5) {
 // Half star
 starView = new ImageView(halfStar);
 } else {
 // Empty star
 starView = new ImageView(emptyStar);
 }

 // Make stars small enough to fit nicely
 starView.setFitHeight(14);
 starView.setFitWidth(14);
 starsBox.getChildren().add(starView);
 }

 return starsBox;
}

/**
 * Calculate the average rating for a specific accommodation
 */
private double calculateAverageRating(int hebergementId) {
 try {
 // You'll need to add a service method to get reviews for a specific accommodation
 // This would typically query your database for reviews
 List<Review> reviews = reviewService.getReviewsByHebergementId(hebergementId);

 if (reviews == null || reviews.isEmpty()) {
 return 0.0; // No reviews yet
 }

 // Calculate the sum of all ratings
 double sum = 0;
 for (Review review : reviews) {
 sum += review.getRating();
 }

 // Return the average
 return sum / reviews.size();
 } catch (Exception e) {
 System.err.println("Error calculating average rating: " + e.getMessage());
 return 0.0;
 }
}
```

