```java:src/main/java/services/ReviewService.java
package services;

import models.Review;
import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;
import java.util.ArrayList;
import java.util.List;
import Util.DataSource;

public class ReviewService {
 private static ReviewService instance;
 private Connection connection;

 private ReviewService() {
 connection = DataSource.getInstance().getCnx();
 }

 public static ReviewService getInstance() {
 if (instance == null) {
 instance = new ReviewService();
 }
 return instance;
 }

 public List<Review> getReviewsByHebergementId(int hebergementId) throws SQLException {
 List<Review> reviews = new ArrayList<>();
 String query = "SELECT * FROM reviews WHERE hebergement_id = ?";

 try (PreparedStatement ps = connection.prepareStatement(query)) {
 ps.setInt(1, hebergementId);
 ResultSet rs = ps.executeQuery();

 while (rs.next()) {
 Review review = new Review();
 review.setId(rs.getInt("id"));
 review.setHebergementId(rs.getInt("hebergement_id"));
 review.setUserId(rs.getInt("user_id"));
 review.setRating(rs.getDouble("rating"));
 review.setComment(rs.getString("comment"));
 review.setDateCreated(rs.getString("date_created"));
 reviews.add(review);
 }
 }

 return reviews;
 }

 // Add other methods as needed (add review, update, delete, etc.)
}
