```java:src/main/java/controllers/ListesHebergController.java
private HBox createStarRating(double rating) {
 HBox starsBox = new HBox(0);
 starsBox.setAlignment(Pos.CENTER);

 // Unicode stars
 String fullStar = "★"; // Full star
 String emptyStar = "☆"; // Empty star

 for (int i = 1; i <= 5; i++) {
 Text starText = new Text();

 if (rating >= i) {
 // Full star
 starText.setText(fullStar);
 starText.setStyle("-fx-fill: #FFD700;"); // Gold color for stars
 } else if (rating >= i - 0.5) {
 // Half star (using full star but with less opacity)
 starText.setText(fullStar);
 starText.setStyle("-fx-fill: #FFD700; -fx-opacity: 0.5;");
 } else {
 // Empty star
 starText.setText(emptyStar);
 starText.setStyle("-fx-fill: #FFD700;");
 }

 starText.setFont(javafx.scene.text.Font.font("System", 14));
 starsBox.getChildren().add(starText);
 }

 return starsBox;
}
