private HBox createRatingDisplay(float rating) {
 HBox ratingBox = new HBox(2);
 ratingBox.setAlignment(Pos.CENTER_RIGHT);

 // Add star icons (5 max)
 for (int i = 1; i <= 5; i++) {
 Text star = new Text();
 if (i <= Math.floor(rating)) {
 // Full star
 star.setText("★");
 star.setStyle("-fx-font-size: 12px; -fx-fill: #FA7335;");
