 maxPriceSlider.setMax(10000);
 maxPriceSlider.setValue(9999);
 maxPriceSlider.setShowTickMarks(true);
 maxPriceSlider.setShowTickLabels(true);
 maxPriceSlider.setMajorTickUnit(2000);

