 Predicate<Hebergements> rangeBetween = h -> 
 h.getPrixHeberg() >= minPriceSlider.getValue() && 
 h.getPrixHeberg() <= maxPriceSlider.getValue();

 items.setPredicate(rangeBetween);
 updateFlowPane();
 }
